INSERT INTO `vaccine` VALUES ('39', '非免疫规划疫苗', 2, '五价轮状病毒疫苗', '1', '3', '未接种', NULL, '五价轮状病毒疫苗，可对五种轮状病毒的不同分型产生预防、防御作用，是预防轮状病毒肠炎的一种疫苗。主要是针对3岁以下的孩子，如果婴儿从出生后没有发生过其病毒感染性的症状，没有发生病毒性腹泻的症状，可以打五价轮状病毒疫苗进行免疫。五价轮状病毒疫苗可起到预防作用，不一定要必须进行接种，取决于具体情况，如胃肠道较好且较少感染病毒，没有接触病毒，可不进行接种。患儿胃肠功能较弱，存在接触病毒的机会，可适当选择接种五价轮状病毒疫苗或其他轮状病毒疫苗。', '0');
