INSERT INTO `vaccine` VALUES ('e97e43be-1c98-45e8-b420-f436522232ad', '免疫规划疫苗', 72, 'A+C群流脑疫苗', '2', '2', '未接种', NULL, '可预防A群，C群脑膜炎球菌引起的流行性脑脊髓膜炎（流脑），属于第一类疫苗，由政府免费提供、无特殊情况应按规定接种。共两剂，3周岁、6周岁各接种1剂。', '5');
INSERT INTO `vaccine` VALUES ('eab269e9-d315-400c-a01b-c8cb52ba04b9', '免疫规划疫苗', 24, '乙脑疫苗', '2', '2', '未接种', NULL, '可预防l流行性乙型脑炎（乙脑）,包括乙脑减病毒活疫苗和乙脑灭活疫苗两种。目前主要接种乙脑减病毒活疫苗，共两剂，于8月龄、两周岁各接种1剂、乙脑灭活疫苗，共4剂，8月龄接种两剂，间隔7~10天,2周岁和66周岁各接种1剂。', '2');
INSERT INTO `vaccine` VALUES ('eaf9a2d9-a40f-4838-911b-d5e53009afab', '免疫规划疫苗', 72, 'A+C群流脑疫苗', '2', '2', '未接种', NULL, '可预防A群，C群脑膜炎球菌引起的流行性脑脊髓膜炎（流脑），属于第一类疫苗，由政府免费提供、无特殊情况应按规定接种。共两剂，3周岁、6周岁各接种1剂。', '2');
INSERT INTO `vaccine` VALUES ('eb736f33-df03-4693-a862-6ebe9c88cdc2', '免疫规划疫苗', 0, '卡介苗', '1', '1', '未接种', NULL, '可预防结核病，特别是结核性脑膜炎和粟粒性结核、属第一类疫苗、由政府免费提供、无特殊情况应按规定接种。儿童出生时接种疫苗。', '0');
INSERT INTO `vaccine` VALUES ('ef7e5e10-afed-4f18-8e00-98e267ebaac1', '免疫规划疫苗', 8, '麻腮风疫苗', '1', '2', '未接种', NULL, '可预防麻疹、流行性腮腺炎、风疹三种儿童常见的急性呼吸道传染病，属于第一类疫苗，由政府免费提供、无特殊情况应按规定接种。2020年6月前，国家免疫规划接种方案为8月龄接种1剂麻风二联疫苗，18月龄接种1剂麻腮风三联疫苗。自2020年6月起，调整为8月龄和18月龄各接种1剂麻腮风疹三联疫苗。', '5');
INSERT INTO `vaccine` VALUES ('f045f00c-7923-4d18-bbc8-a686ef56a2d2', '免疫规划疫苗', 0, '乙肝疫苗', '1', '3', '未接种', NULL, '可预防已型病毒性肝炎(乙肝)，属第一类疫苗、由政府免费提供、无特殊情况应按规定接种。共三剂，儿童出生后24小时内接种第一剂，之后在一月龄和6月龄各接种1剂。', '5');
INSERT INTO `vaccine` VALUES ('f4b359d1-7c10-40f4-9985-5d043f269c50', '免疫规划疫苗', 18, '甲肝疫苗', '1', '1', '未接种', NULL, '可预防甲型病毒性肝炎（甲肝），包括甲肝减病毒活疫苗和甲肝灭活疫苗两种。甲肝减病毒活疫苗，18月龄接种一次，甲肝灭活疫苗，共两剂，18月龄和24月龄各接种1剂。', '2');
INSERT INTO `vaccine` VALUES ('f51ce05c-f257-497a-bacf-fc4be1af95bc', '非免疫规划疫苗', 2, 'Hib疫苗', '1', '4', '未接种', NULL, 'HIB疫苗又称为HIB多糖蛋白结合疫苗，是B型流感嗜血杆菌结合疫苗的简称。通过接种HIB疫苗，可以有效预防B型流感嗜血杆菌引起的脑膜炎、肺炎、败血症、蜂窝组织炎、关节炎、咽喉炎这些疾病的发生。B型流感嗜血杆菌结合疫苗属于国家二类疫苗，是自费疫苗，可以自愿的接种。一般适龄儿童可以及时的接种疫苗，建议满两个月到五周岁的儿童，可以在孩子健康的时候进行及时的接种。接种的时候建议从两个月或三个月开始进行接种，每隔一个月或两个月接种一次，总共接种三次。在孩子18月的时候，加强免疫接种一次，一共需要接种四次', '0');
INSERT INTO `vaccine` VALUES ('f6182594-e3c9-488b-b5da-a8422adc0312', '免疫规划疫苗', 6, 'A群流脑疫苗', '1', '2', '未接种', NULL, '可预防由A群脑膜炎球菌引起的流行性脑脊髓膜炎，属于第一类疫苗，由政府免费提供、无特殊情况应按规定接种。共2剂，6月龄和9月龄各接种1剂。', '7');
